import React, { useState, useEffect } from 'react';
import axiosInstance from '../config/axiosInstance';
import { API_BASE_URL } from '../config';

const Order = ({ userId }) => {
  const [pizzas, setPizzas] = useState([]);
  const [selectedPizzasId, setSelectedPizzasId] = useState([]);

  useEffect(() => {
    const fetchPizzas = async () => {
      try {
        const response = await axiosInstance.get(`${API_BASE_URL}/pizzas`);
        setPizzas(response.data);
      } catch (error) {
        console.error('Error fetching pizzas:', error);
      }
    };

    fetchPizzas();
  }, []);

  const handlePizzaChange = (pizza) => {
    setSelectedPizzasId((prevSelected) =>
      prevSelected.includes(pizza.id)
        ? prevSelected.filter((id) => id !== pizza.id)
        : [...prevSelected, pizza.id]
    );
  };

  const handleSubmit = () => {
    axiosInstance
      .post(`${API_BASE_URL}/orders/create`, {
        userId: localStorage.getItem('userId'),
        pizzas: selectedPizzasId,
      })
      .then((response) => {
        alert('Order Created Successfully');
        console.log(response.data.order);
      })
      .catch((error) => console.error('Error creating order:', error));
  };

  return (
    <div className='container mx-auto p-4'>
      <h2 className='text-3xl font-bold mb-6 text-center'>Create Order</h2>
      <div className='grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4'>
        {pizzas.map((pizza) => (
          <div
            key={pizza.id}
            className='bg-white shadow-md rounded-lg p-4 border border-gray-200'
          >
            <div className='flex items-center'>
              <input
                type='checkbox'
                value={pizza.id}
                onChange={() => handlePizzaChange(pizza)}
                className='mr-2'
              />
              <div>
                <h3 className='text-lg font-semibold'>{pizza.name}</h3>
                <p className='text-gray-600'>
                  Toppings: {pizza.toppings.map((t) => t.topping.name).join(', ')}
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>
      <div className='text-center mt-6'>
        <button
          onClick={handleSubmit}
          className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded'
        >
          Place Order
        </button>
      </div>
    </div>
  );
};

export default Order;
